import React, { useState, useMemo } from 'react';
import FiltersPanel from './FiltersPanel';
import RouteTable from './RouteTable';
import { FilterState, LaneData, Route } from '../types';
import { destinations, containerTypes, getLaneData } from '../mockData';

const RouteDashboard: React.FC = () => {
  const [filters, setFilters] = useState<FilterState>({
    destination: destinations[0],
    containerType: containerTypes[0],
  });

  const [appliedFilters, setAppliedFilters] = useState<FilterState>(filters);

  const handleFilterChange = (field: keyof FilterState, value: string) => {
    setFilters((prev) => ({ ...prev, [field]: value }));
  };

  const handleApply = () => {
    setAppliedFilters(filters);
  };

  const laneData: LaneData | undefined = useMemo(() => {
    return getLaneData(appliedFilters.destination, appliedFilters.containerType);
  }, [appliedFilters]);

  const topRoutes: Route[] = useMemo(() => {
    if (!laneData) return [];
    return laneData.routes.slice(0, 3);
  }, [laneData]);

  const worstRoute: Route | undefined = useMemo(() => {
    if (!laneData || laneData.routes.length === 0) return undefined;
    return laneData.routes[laneData.routes.length - 1];
  }, [laneData]);

  return (
    <>
      {/* Header */}
      <header className="header">
        <h1 className="header-title">Freight Route Optimizer</h1>
        <p className="header-subtitle">
          Busan (KR) → POD → Inland Destination
        </p>
      </header>

      {/* Filters */}
      <FiltersPanel
        destinations={destinations}
        containerTypes={containerTypes}
        filters={filters}
        onFilterChange={handleFilterChange}
        onApply={handleApply}
      />

      {/* Results */}
      <section className="results-section" aria-label="Route Results">
        {laneData ? (
          <>
            <RouteTable
              title="Top 3 Recommended Routes"
              routes={topRoutes}
              currency={laneData.currency}
              variant="top"
            />

            {worstRoute && (
              <RouteTable
                title="Highest Cost Route"
                routes={[worstRoute]}
                currency={laneData.currency}
                variant="worst"
              />
            )}
          </>
        ) : (
          <div className="card">
            <div className="empty-state">
              No route data available for the selected criteria.
            </div>
          </div>
        )}
      </section>
    </>
  );
};

export default RouteDashboard;
