import React from 'react';

interface RouteMapProps {
  pod: string;
  destination: string;
}

// Helper to build Google Maps directions URL
function getGoogleMapsEmbedUrl(pod: string, destination: string) {
  const base = 'https://www.google.com/maps/embed/v1/directions';
  const apiKey = 'YOUR_GOOGLE_MAPS_API_KEY'; // <-- Replace with your API key
  const origin = encodeURIComponent(pod);
  const dest = encodeURIComponent(destination);
  return `${base}?key=${apiKey}&origin=${origin}&destination=${dest}`;
}

const RouteMap: React.FC<RouteMapProps> = ({ pod, destination }) => {
  if (!pod || !destination) return null;
  
  const apiKey = 'YOUR_GOOGLE_MAPS_API_KEY';
  
  // Show placeholder if API key not configured
  if (apiKey === 'YOUR_GOOGLE_MAPS_API_KEY') {
    return (
      <div className="route-map-container">
        <div style={{ 
          height: 350, 
          display: 'flex', 
          alignItems: 'center', 
          justifyContent: 'center',
          background: '#f5f5f5',
          borderRadius: 8,
          color: '#666',
          textAlign: 'center',
          padding: 20
        }}>
          <div>
            <p><strong>Google Maps API Key Required</strong></p>
            <p style={{ fontSize: 14 }}>Add your API key in RouteMap.tsx to view the route map</p>
          </div>
        </div>
        <div className="map-caption">
          <strong>Route:</strong> {pod} → {destination}
        </div>
      </div>
    );
  }
  
  // NOTE: You must enable Maps Embed API in your Google Cloud project and provide your API key above.
  const url = getGoogleMapsEmbedUrl(pod, destination);
  return (
    <div className="route-map-container">
      <iframe
        title="Route Map"
        width="100%"
        height="350"
        frameBorder="0"
        style={{ border: 0, borderRadius: 8 }}
        src={url}
        allowFullScreen
      />
      <div className="map-caption">
        <strong>Route:</strong> {pod} → {destination}
      </div>
    </div>
  );
};

export default RouteMap;
