import React from 'react';
import { Route } from '../types';

interface RouteTableProps {
  title: string;
  routes: Route[];
  currency: string;
  variant: 'top' | 'worst';
}

const formatRate = (rate: number, currency: string): string => {
  return `${currency} ${rate.toLocaleString('en-US', {
    minimumFractionDigits: 0,
    maximumFractionDigits: 0,
  })}`;
};

const RouteTable: React.FC<RouteTableProps> = ({
  title,
  routes,
  currency,
  variant,
}) => {
  const cardClass = variant === 'worst' ? 'card card--worst' : 'card';
  const badgeClass = variant === 'worst' ? 'rank-badge rank-badge--worst' : 'rank-badge rank-badge--top';

  return (
    <div className={cardClass}>
      <div className="card-header">
        <h2 className="card-title">{title}</h2>
      </div>
      <div className="card-body">
        <div className="route-table-container">
          <table className="route-table">
            <thead>
              <tr>
                <th className="col-rank">Rank</th>
                <th>POD (Intermediate Port)</th>
                <th>Transport Mode</th>
                <th className="col-rate">Total Rate</th>
              </tr>
            </thead>
            <tbody>
              {routes.map((route) => (
                <tr key={route.rank}>
                  <td className="col-rank">
                    <span className={badgeClass}>{route.rank}</span>
                  </td>
                  <td>{route.pod}</td>
                  <td>{route.mode}</td>
                  <td className="col-rate">
                    <span className="rate-value">
                      {formatRate(route.totalRate, currency)}
                    </span>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default RouteTable;
