import React from 'react';
import {
  generateGoogleMapsDirectionsUrl,
  generateGoogleMapsEmbedUrl,
  cleanLocationString,
} from '../utils/googleMapsHelper';

interface RouteMapProps {
  pod: string;
  destination: string;
}

const RouteMap: React.FC<RouteMapProps> = ({ pod, destination }) => {
  if (!pod || !destination) return null;
  
  const mapsUrl = generateGoogleMapsDirectionsUrl(pod, destination);
  const embedUrl = generateGoogleMapsEmbedUrl(pod, destination);
  const cleanPod = cleanLocationString(pod);
  const cleanDest = cleanLocationString(destination);
  
  const handleViewRoute = () => {
    window.open(mapsUrl, '_blank', 'noopener,noreferrer');
  };
  
  return (
    <div className="route-map-container">
      <div className="route-info">
        <h3 className="route-info-title">Route Information</h3>
        <div className="route-path">
          <div className="route-location">
            <span className="route-label">From (POD):</span>
            <span className="route-value">{cleanPod}</span>
          </div>
          <div className="route-arrow">â†“</div>
          <div className="route-location">
            <span className="route-label">To (Destination):</span>
            <span className="route-value">{cleanDest}</span>
          </div>
        </div>
        <button 
          className="btn-view-route"
          onClick={handleViewRoute}
          type="button"
        >
          <svg 
            width="16" 
            height="16" 
            viewBox="0 0 24 24" 
            fill="none" 
            stroke="currentColor" 
            strokeWidth="2"
            style={{ marginRight: 8 }}
          >
            <path d="M21 10c0 7-9 13-9 13s-9-6-9-13a9 9 0 0 1 18 0z"/>
            <circle cx="12" cy="10" r="3"/>
          </svg>
          View Route on Google Maps
        </button>
        <p className="route-note">
          Opens Google Maps in a new tab with directions from {cleanPod} to {cleanDest}
        </p>
      </div>

      <div className="map-embed-wrapper">
        <iframe
          title={`Route from ${cleanPod} to ${cleanDest}`}
          src={embedUrl}
          className="map-embed"
          loading="lazy"
          allowFullScreen
          referrerPolicy="no-referrer-when-downgrade"
        />
        <p className="map-caption">
          Inline map preview. If the embed is blocked, use the button above to open Google Maps.
        </p>
      </div>
    </div>
  );
};

export default RouteMap;
